import React, { useState, useEffect } from 'react';
import Slider from 'react-slider';

function Blog() {
    const [Items, setItems] = useState([
        { id: 1, title: 'First Item', content: 'This is the first item.', price: 10, category: 'Books' },
        { id: 2, title: 'Second Item', content: 'This is the second item.', price: 20, category: 'Electronics' }
    ]);
    const [searchText, setSearchText] = useState('');
    const [priceRange, setPriceRange] = useState([0, getMaxPrice()]);
    const [selectedCategories, setSelectedCategories] = useState([]);

    useEffect(() => {
        setPriceRange([0, getMaxPrice()]);
    }, [Items]);

    function fetchItems() {
        fetch('http://localhost:3001/Sale')
            .then((res) => res.json())
            .then((data) => setItems(data))
            .catch((error) => console.error('Error fetching items:', error));
    }

    useEffect(() => {
        fetchItems();
    }, []);

    const handleSearch = (e) => {
        setSearchText(e.target.value);
    };

    const handlePriceChange = (value) => {
        setPriceRange(value);
    };

    const handleCategoryChange = (e) => {
        const category = e.target.value;
        if (selectedCategories.includes(category)) {
            setSelectedCategories(selectedCategories.filter((cat) => cat !== category));
        } else {
            setSelectedCategories([...selectedCategories, category]);
        }
    };

    const filteredItems = Items.filter((item) =>
        (item.title.toLowerCase().includes(searchText.toLowerCase()) ||
            item.content.toLowerCase().includes(searchText.toLowerCase())) &&
        item.price >= priceRange[0] &&
        item.price <= priceRange[1] &&
        (selectedCategories.length === 0 || selectedCategories.includes(item.category))
    );

    function getMaxPrice() {
        const prices = Items.map((item) => item.price).filter((price) => typeof price === 'number' && !isNaN(price));
        return prices.length > 0 ? Math.max(...prices) : 0;
    }

    const sliderStyles = {
        container: {
            marginTop: '20px',
            marginBottom: '20px',
        },
        bar: {
            height: '5px',
            backgroundColor: '#ccc',
        },
        handle: {
            width: '20px',
            height: '20px',
            borderRadius: '50%',
            backgroundColor: '#007bff',
            cursor: 'grab',
        },
        handleActive: {
            cursor: 'grabbing',
        },
        handleFocus: {
            outline: 'none',
        },
        handleHover: {
            backgroundColor: '#0056b3',
        },
        value: {
            marginTop: '5px',
            fontSize: '14px',
            color: '#333',
        },
    };

    return (
        <div>
            <h2>Items for sale:</h2>
            <input
                type="text"
                placeholder="Search posts..."
                value={searchText}
                onChange={handleSearch}
            />
            <div style={sliderStyles.container}>
                <label>Price Range:</label>
                <Slider
                    min={0}
                    max={getMaxPrice()}
                    defaultValue={priceRange}
                    onChange={handlePriceChange}
                    withBars
                    renderTrack={({ props, children }) => (
                        <div
                            {...props}
                            style={{ ...props.style, ...sliderStyles.bar }}
                        >
                            {children}
                        </div>
                    )}
                    renderThumb={({ props, isDragged }) => (
                        <div
                            {...props}
                            style={{
                                ...props.style,
                                ...sliderStyles.handle,
                                ...(isDragged ? sliderStyles.handleActive : null),
                            }}
                        />
                    )}
                />
                <span style={sliderStyles.value}>${priceRange[0]} - ${priceRange[1]}</span>
            </div>
            <div>
                <label>Category:</label>
                <div>
                    <label><input type="checkbox" value="Sale" onChange={handleCategoryChange} checked={selectedCategories.includes('Sale')} /> For Sale</label>
                    <label><input type="checkbox" value="Wanted" onChange={handleCategoryChange} checked={selectedCategories.includes('Wanted')} /> Wanted</label>
                    <label><input type="checkbox" value="AcademicService" onChange={handleCategoryChange} checked={selectedCategories.includes('AcademicService')} /> Academic Service</label>
                </div>
            </div>
            {filteredItems.map((post) => (
                <div key={post.id}>
                    <h3>{post.title}</h3>
                    <p>Content: {post.content}</p>
                    <p>Price: ${post.price}</p>
                    <p>Category: {post.category}</p>
                    <img src={post.pictureURL} alt={post.title} style={{ maxWidth: '200px', maxHeight: '200px', borderRadius: '5px' }} />
                </div>
            ))}
        </div>
    );
}

export default Blog;
