import React, { useState } from 'react';
import firebase from 'firebase';
import 'firebase/storage';

// Initialize Firebase
const firebaseConfig = {
    apiKey: "AIzaSyB0ESvN7O6EwyI98AAIw-jdFRaud5qGLQQ",
    authDomain: "cps630-project-ba3eb.firebaseapp.com",
    projectId: "cps630-project-ba3eb",
    storageBucket: "cps630-project-ba3eb.appspot.com",
    messagingSenderId: "1010540454168",
    appId: "1:1010540454168:web:2ff8c42b873ef28f47fe6d",
    measurementId: "G-EQWD2H27HV"
};

firebase.initializeApp(firebaseConfig);



function NewPost({ onAddPost }) {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [picture, setPicture] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Create a new FormData object to store form data
        const formData = new FormData();
        formData.append('title', title);
        formData.append('content', content);
        formData.append('picture', picture);

        // Upload picture to Firebase Storage
        const storageRef = firebase.storage().ref();
        const fileRef = storageRef.child(`images/${picture.name}`);
        await fileRef.put(picture);

        // Get download URL for the uploaded picture
        const pictureURL = await fileRef.getDownloadURL();

        // Add picture URL to the form data
        formData.append('pictureURL', pictureURL);

        // Call the onAddPost function with the FormData object
        onAddPost(formData);

        // Reset form fields
        setTitle('');
        setContent('');
        setPicture(null);
    };

    return (
        <div>
            <h2>Add a New Post</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Title:</label>
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </div>
                <div>
                    <label>Content:</label>
                    <textarea value={content} onChange={(e) => setContent(e.target.value)}></textarea>
                </div>
                <div>
                    <label>Picture:</label>
                    <input type="file" accept="image/*" onChange={(e) => setPicture(e.target.files[0])} />
                </div>
                <button type="submit">Add Post</button>
            </form>
        </div>
    );
}

export default NewPost;
