import React, { useState, useEffect } from 'react';

function Blog() {
    const [items, setItems] = useState([]);
    const [filteredItems, setFilteredItems] = useState([]);
    const [priceFilter, setPriceFilter] = useState(0);
    const [categoryFilter, setCategoryFilter] = useState('');
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        fetch('http://localhost:3001/Sale')
            .then((res) => res.json())
            .then((data) => {
                setItems(data);
                setFilteredItems(data);
                // Calculate the maximum price from the fetched items
                const maxPrice = Math.max(...data.map(item => item.price));
                setPriceFilter(maxPrice);
            })
            .catch((error) => console.error('Error fetching data:', error));
    }, []);

    const handlePriceFilterChange = (e) => {
        const newPriceFilter = parseInt(e.target.value);
        setPriceFilter(newPriceFilter);
        filterItems(categoryFilter, searchQuery, newPriceFilter);
    };

    const handleCategoryFilterChange = (e) => {
        const newCategoryFilter = e.target.value;
        setCategoryFilter(newCategoryFilter);
        filterItems(newCategoryFilter, searchQuery, priceFilter);
    };

    const handleSearchInputChange = (e) => {
        const newSearchQuery = e.target.value;
        setSearchQuery(newSearchQuery);
        filterItems(categoryFilter, newSearchQuery, priceFilter);
    };

    const filterItems = (category, search, price) => {
        let filtered = items.filter(item => item.price <= price);
        if (category) {
            filtered = filtered.filter(item => item.category === category);
        }
        if (search) {
            filtered = filtered.filter(item => item.city.toLowerCase().includes(search.toLowerCase()));
        }
        setFilteredItems(filtered);
    };

    return (
        <div>
            <h2>Items for sale:</h2>
            <div>
                <label htmlFor="priceFilter">Price Filter:</label>
                <input
                    type="range"
                    id="priceFilter"
                    name="priceFilter"
                    min="0"
                    max={priceFilter}
                    value={priceFilter}
                    onChange={handlePriceFilterChange}
                />
                <span>${priceFilter}</span>
            </div>
            <div>
                <label>Category Filter:</label><br />
                <input type="radio" id="all" name="category" value="" checked={categoryFilter === ''} onChange={handleCategoryFilterChange} />
                <label htmlFor="all">All</label><br />
                <input type="radio" id="sale" name="category" value="Sale" checked={categoryFilter === 'Sale'} onChange={handleCategoryFilterChange} />
                <label htmlFor="sale">Sale</label><br />
                <input type="radio" id="wanted" name="category" value="Wanted" checked={categoryFilter === 'Wanted'} onChange={handleCategoryFilterChange} />
                <label htmlFor="wanted">Wanted</label><br />
                {/* Add more radio buttons for other categories */}
            </div>
            <div>
                <label htmlFor="locationSearch">Search by Location:</label>
                <input
                    type="text"
                    id="locationSearch"
                    value={searchQuery}
                    onChange={handleSearchInputChange}
                />
            </div>
            {filteredItems.map((post) => (
                <div key={post.id}>
                    <h3>{post.title}</h3>
                    <p>Content: {post.content}</p>
                    <p>City: {post.city}</p>
                    <p>Price: ${post.price}</p>
                    <p>Category: {post.category}</p>
                    <img src={post.pictureURL} alt={post.title} style={{ maxWidth: '200px', maxHeight: '200px', borderRadius: '5px' }} />
                </div>
            ))}
        </div>
    );
}

export default Blog;
