import React, { useState, useEffect } from 'react';

function Blog() {
    const [items, setItems] = useState([]);
    const [filteredItems, setFilteredItems] = useState([]);
    const [search, setSearch] = useState('');
    const [selectedCategories, setSelectedCategories] = useState([]);
    const [maxPrice, setMaxPrice] = useState(0);

    useEffect(() => {
        fetch('http://localhost:3001/Sale')
            .then((res) => res.json())
            .then((data) => {
                setItems(data);
                setFilteredItems(data);
                setMaxPrice(Math.max(...data.map(item => item.price)));
            })
            .catch((error) => console.error('Error fetching data:', error));
    }, []);

    const filterItems = (categories, search, price) => {
        let filtered = items.filter(item => item.price <= price);
        if (categories.length > 0) {
            filtered = filtered.filter(item => categories.includes(item.category));
        }
        if (search) {
            const searchTerm = search.toLowerCase();
            filtered = filtered.filter(item => (
                item.title.toLowerCase().includes(searchTerm) || 
                item.content.toLowerCase().includes(searchTerm)
            ));
        }
        setFilteredItems(filtered);
    };

    useEffect(() => {
        filterItems(selectedCategories, search, maxPrice);
    }, [selectedCategories, search, maxPrice]);

    const handleCategoryChange = (category) => {
        if (selectedCategories.includes(category)) {
            setSelectedCategories(selectedCategories.filter(cat => cat !== category));
        } else {
            setSelectedCategories([...selectedCategories, category]);
        }
    };

    return (
        <div>
            <h2>Items for sale:</h2>
            <div>
                <input
                    type="text"
                    placeholder="Search by title or content"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />
                <div>
                    <h3>Filter by Category:</h3>
                    <label>
                        <input
                            type="checkbox"
                            checked={selectedCategories.includes('Wanted')}
                            onChange={() => handleCategoryChange('Wanted')}
                        />
                        Wanted
                    </label>
                    <label>
                        <input
                            type="checkbox"
                            checked={selectedCategories.includes('Sale')}
                            onChange={() => handleCategoryChange('Sale')}
                        />
                        Sale
                    </label>
                    <label>
                        <input
                            type="checkbox"
                            checked={selectedCategories.includes('AcademicService')}
                            onChange={() => handleCategoryChange('AcademicService')}
                        />
                        Academic Service
                    </label>
                </div>
                <div>
                    <h3>Filter by Price:</h3>
                    <input
                        type="range"
                        min="0"
                        max={maxPrice}
                        value={maxPrice}
                        onChange={(e) => setMaxPrice(parseInt(e.target.value))}
                    />
                    <span>${maxPrice}</span>
                </div>
            </div>
            {filteredItems.map((item) => (
                <div key={item.id}>
                    <h3>{item.title}</h3>
                    <p>Content: {item.content}</p>
                    <p>City: {item.city}</p>
                    <p>Price: ${item.price}</p>
                    <p>Category: {item.category}</p>
                    <img src={item.pictureURL} alt={item.title} style={{ maxWidth: '200px', maxHeight: '200px', borderRadius: '5px' }} />
                </div>
            ))}
        </div>
    );
}

export default Blog;
