import React, { useState, useEffect } from 'react';
import logo from "../../assets/tmu_logo.svg";
import "./Navbar.css";

function Navbar() {
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    fetch("http://localhost:3001/Users")
      .then((res) => res.json())
      .then((data) => {
        // Assuming data is an array of users, find the current user
        const currentUser = data.find(user => user.username === sessionStorage.getItem("username"));
        if (currentUser && currentUser.isAdmin) {
          setIsAdmin(true);
        }
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <>
      <div className="navbar">
        <nav>
          <div className="logo">
            <img src={logo} alt="TMU Logo" width={100} height={100} />
            <h1 className="sitename">UniFynd</h1>
          </div>
          <button className="hamburger-btn" onClick={handleClick}>
            â˜°
          </button>
          <ul className="large-links">
            <li>
              <a href="/Home">Home</a>
            </li>
            <li>
              <a href="/AddItem">Add Post</a>
            </li>
            {isAdmin && (
              <li>
                <a href="/Admin">Admin</a>
              </li>
            )}
            <li>
              <a href="/Profile">Profile</a>
            </li>
            <li>
              <a href="/Inbox">Inbox</a>
            </li>
            <li>
              <a href="/">Logout</a>
            </li>
          </ul>
        </nav>
        <div className="mobile-nav">
          <ul>
            <li>
              <a href="/Home">Home</a>
            </li>
            <li>
              <a href="/AddItem">Add Post</a>
            </li>
            {isAdmin && (
              <li>
                <a href="/Admin">Admin</a>
              </li>
            )}
            <li>
              <a href="/Profile">Profile</a>
            </li>
            <li>
              <a href="/Inbox">Inbox</a>
            </li>
            <li>
              <a href="/">Logout</a>
            </li>
          </ul>
        </div>
      </div>
    </>
  );
}

export default Navbar;
