import { set } from 'mongoose';
import React, { useEffect, useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/storage';
import './NewPost.css'; // Import CSS file for styling

const firebaseConfig = {
    // Your Firebase config
};

firebase.initializeApp(firebaseConfig);

const weatherAPIKey = '9f6433c26012296c716edeafb0bfabd4';

function NewPost({ onAddPost }) {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [picture, setPicture] = useState('');
    const [category, setCategory] = useState('');
    const [price, setPrice] = useState('');
    const [city, setCity] = useState('');
    const [suggestedCities, setSuggestedCities] = useState([]);
    const [showDropdown, setShowDropdown] = useState(false); // State variable to control dropdown visibility

    const handleSubmit = (e) => {
        e.preventDefault();
        const newPost = { id: Date.now(), title, content, price, pictureURL: picture, category };
        onAddPost(newPost);
        setTitle('');
        setContent('');
        setPicture('');
        setCategory('');
        setPrice('');
    };

    const fetchCities = async (searchText) => {
        try {
            const response = await fetch(`https://api.openweathermap.org/geo/1.0/direct?q=${searchText}&limit=5&appid=${weatherAPIKey}`);
            if (!response.ok) {
                throw new Error('Failed to fetch cities');
            }
            const data = await response.json();
            const ontarioCities = data.filter(city => city.country === 'CA' && city.state === 'Ontario');
            setSuggestedCities(ontarioCities);
            setShowDropdown(true); // Show dropdown when there are suggested cities
        } catch (error) {
            console.error(error);
            setSuggestedCities([]);
            setShowDropdown(false); // Hide dropdown on error
        }
    };

    const handleSelectCity = (selectedCity) => {
        setCity(selectedCity);
        setShowDropdown(false); // Hide dropdown when a city is selected
    };

    const handleFileUpload = (event) => {
        const file = event.target.files[0];
        const uniqueFileName = `${file.name}-${Date.now()}`;
        const storageRef = firebase.storage().ref();
        const fileRef = storageRef.child(uniqueFileName);
        fileRef.put(file)
            .then(() => {
                console.log('Uploaded a file');
                return fileRef.getDownloadURL();
            })
            .then((url) => {
                console.log('URL:', url);
                setPicture(url);
            })
            .catch((error) => {
                console.error('Error uploading file:', error);
            });
    };

    return (
        <div>
            <h2>Add a New Post</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Title:</label>
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </div>
                <div>
                    <label>City:</label>
                    <input
                        type="text"
                        value={city}
                        onChange={(e) => {
                            setCity(e.target.value);
                            fetchCities(e.target.value);
                        }}
                    />
                    {showDropdown && suggestedCities.length > 0 && (
                        <ul className="dropdown-container"> {/* Apply dropdown class */}
                            {suggestedCities.map((city) => (
                                <li key={city.name} className="dropdown-item" onClick={() => handleSelectCity(city.name)}> {/* Apply dropdown item class */}
                                    {city.name}, {city.country}
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
                <div>
                    <label>Content:</label>
                    <textarea value={content} onChange={(e) => setContent(e.target.value)}></textarea>
                </div>
                <div>
                    <label>Price: $</label>
                    <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} />
                </div>

                <div>
                    <label>Picture:</label>
                    <input type="file" accept="image/*" onChange={(e) => handleFileUpload(e)} />
                </div>
                <div>
                    <label>Category:</label>
                    <select value={category} onChange={(e) => setCategory(e.target.value)}>
                        <option value="Wanted">Wanted</option>
                        <option value="Sale">Sale</option>
                        <option value="AcademicService">Academic Service</option>
                    </select>
                </div>
                <button type="submit">Add Post</button>
            </form>
        </div>
    );
}

export default NewPost;
