import { set } from 'mongoose';
import React, { useState } from 'react';
import firebase from 'firebase/compat/app';
import { getStorage, ref, uploadBytes, getDownloadURL } from 'firebase/compat/storage';

const firebaseConfig = {
    apiKey: "AIzaSyB0ESvN7O6EwyI98AAIw-jdFRaud5qGLQQ",
    authDomain: "cps630-project-ba3eb.firebaseapp.com",
    projectId: "cps630-project-ba3eb",
    storageBucket: "cps630-project-ba3eb.appspot.com",
    messagingSenderId: "1010540454168",
    appId: "1:1010540454168:web:2ff8c42b873ef28f47fe6d",
    measurementId: "G-EQWD2H27HV"
};

firebase.initializeApp(firebaseConfig);

function NewPost({ onAddPost }) {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [picture, setPicture] = useState('');
    const handleSubmit = (e) => {
        e.preventDefault();
        const newPost = { id: Date.now(), title, content, picture };
        onAddPost(newPost);
        setTitle('');
        setContent('');
        setPicture('');
    };

    // WITH MongoDB
    function onAddPost(newPost) {
        fetch('http://localhost:3001/posts', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(newPost)
        })
            .then((res) => res.json())
            .then((data) => {
                alert('Post submitted successfully!');
            });
    }

    const handleFileUpload = async (event) => {
        const selectedFile = event.target.files[0];
        if (selectedFile) {
            const storage = getStorage(firebase); // Initialize Firebase Storage
            const storageRef = ref(storage, `images/${selectedFile.name}`); // Get reference to the file path
            await uploadBytes(storageRef, selectedFile); // Upload the file to Firebase Storage
            const pictureURL = await getDownloadURL(storageRef); // Get the download URL of the uploaded file
            console.log(pictureURL); // Log the URL to the console
            setPicture(pictureURL); // Set the picture URL in the state
        } else {
            console.log('No file selected');
        }
    }

        return (
            <div>
                <h2>Add a New Post</h2>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>Title:</label>
                        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                    </div>
                    <div>
                        <label>Content:</label>
                        <textarea value={content} onChange={(e) => setContent(e.target.value)}></textarea>
                    </div>
                    <div>
                        <label>Picture:</label>
                        <input type="file" accept="image/*" onChange={(e) => handleFileUpload(e)} />
                    </div>
                    <button type="submit">Add Post</button>
                </form>
            </div>
        );
    }

    export default NewPost;
